const modal=document.getElementById("myModal"),btn=document.getElementById("button"),closeButton=document.getElementsByClassName("close")[0],counterDiv=document.getElementById("counter"),reset=document.getElementById("reset-button");btn.onclick=()=>{var e=Number(localStorage.getItem("value"));modal.style.display="block",reset.style.display="none",4<=e&&(reset.style.display="block"),localStorage.setItem("value",e+1),counterDiv.innerHTML=localStorage.getItem("value")},reset.onclick=()=>{counterDiv.innerHTML=0,localStorage.setItem("value","0"),reset.style.display="none"},closeButton.onclick=()=>{modal.style.display="none"},window.onclick=e=>{e.target==modal&&(modal.style.display="none")};